import React, { Component } from 'react'
import PureComp from './PureComp'
import RegComp from './RegComp'

class ParentComp extends Component {
    constructor(props) {
      super(props)

      this.state = {
         name: 'Sahil'
      }
    }
    componentDidUpdate(){
        setInterval(()=>{
            this.setState({
                name:'Sahil'
            })
        },2000)
    }
  render() {
    return (
      <div>
        console.log('Log from Parent')
        <PureComp props={this.state.name}></PureComp>
        <RegComp props={this.state.name}></RegComp>
      </div>
    )
  }
}

export default ParentComp
